export default function TailButton({caption, bcolor, handleClick})
{
    const colorB = {
        'blue' : 'bg-blue-600',
        'orange' : 'bg-orange-600'
    }

    const colorBHober = {
        'blue' : 'hover:bg-blue-800',
        'oragne' : 'hover:bg-orange-800'
    }

    return (
        <button className={`inline-flex px-5 py-3 rounded-md mx-2 justify-center items-center text-white font-bold ${colorB[bcolor]} ${colorBHover[bcolor]}`} onClick={handleClick}>
            {caption}
        </button>
    );
}
