import { useState, useEffect } from 'react';
import TrafficNav from './TrafficNav';

export default function Traffic() 
{
  // 전체 fetch 데이터
  const [tdata, setTdata] = useState();

  // 대분류 데이터
  const [c1, setC1] = useState();
  // 선택된 대분류
  const [selC1, setSelC1] = useState();
  // 중분류 데이터
  const [c2, setC2] = useState();
  //선택된 중분류
  const [selC2, setSelC2] = useState();

  // 상세정보
  const [info, setInfo] = useState();

  const getFetchData = () => {
    let url = `https://api.odcloud.kr/api/15070282/v1/uddi:34f1f0b1-1289-49db-be1b-a4566880bb97?page=1&perPage=18&serviceKey=${process.env.REACT_APP_API_KEY}`;
    console.log(url);

    fetch(url)
     .then(resp => resp.json()) // 정상작동
     .then(data => setTdata(data.data)) // 데이터 저장해놓기
     .catch(err => console.log(err)) // 오류발생시
  };

  // 컴포넌트 생성시 fetch
  useEffect(() => {
  getFetchData();    
  }, []); // dependency array가 공백이면 한번만 호출

  // tdata가 변경되었을 때
  useEffect(() => {
    if (!tdata) return; // tdata 없을 때는 바로 리턴

    // 대분류 만들기
    let tm = tdata.map( item => item['사고유형대분류'] );
    tm = [... new Set(tm)];
    setC1(tm);
  }, [tdata]);

  useEffect(() => {
    if (!tdata || !c1 || !selC1) return;

    // 중분류 만들기
    let tm = tdata.filter(item => item['사고유형대분류'] === selC1).map(item => item['사고유형']);
    setC2(tm);

  }, [selC1]);

  useEffect(() => {
    if (!tdata || !c1 || !selC2 || !selC2) return;

    let tm = tdata.filter(item => item['사고유형대분류'] === selC1 && item['사고유형'] === selC2)[0];
    //console.log('선택 항목들', tm);
    const infoKey = ['사고건수', '사망자수', '중상자수', '경상자수', '부상신고자수'];

    tm = infoKey.map(item => 
    <div key={item}>
      <div>{item}</div>
      <div>{tm[item]}</div>
    </div>)
    setInfo(tm);
  }, [selC2]);

  return (
    <div className="w-full h-full flex flex-col justify-start items-center">
      {c1 && <TrafficNav title={'대분류'} c={c1} sel={selC1} setSel={setSelC1} />}
      {c2 && <TrafficNav title={'사고유형'} c={c2} sel = {selC2} setSel = {setSelC2} />}
      <div className="w-full grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-2 ">
        {info}
      </div>
    </div>
  )
}
